import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./BrandList.css";

function BrandList() {
  const navigate = useNavigate();

  // Initial Brand List Data
  const [brands] = useState([
    {
      id: 1,
      name: "Computers",
      image: "data:image/jpeg;base64,",
      Description: "Laptop",
    },
    {
      id: 2,
      name: "Addidas",
      image: "data:image/png;base64,",
      Description: "good",
    },
    {
      id: 3,
      name: "Vegetables",
      image: "data:image/jpeg;base64,",
      Description: "Organic vegetables",
    },
    
  ]);

  const [searchTerm, setSearchTerm] = useState("");
  const [sortConfig, setSortConfig] = useState({ key: null, direction: "asc" });

  // Filtered and Sorted Brands
  const filteredBrands = brands.filter(
    (brand) =>
      brand.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      brand.Description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const sortedBrands = [...filteredBrands].sort((a, b) => {
    if (!sortConfig.key) return 0;
    if (a[sortConfig.key] < b[sortConfig.key]) return sortConfig.direction === "asc" ? -1 : 1;
    if (a[sortConfig.key] > b[sortConfig.key]) return sortConfig.direction === "asc" ? 1 : -1;
    return 0;
  });

  // Sorting Functionality
  const sortData = (key) => {
    setSortConfig((prev) => {
      const direction = prev.key === key && prev.direction === "asc" ? "desc" : "asc";
      return { key, direction };
    });
  };

  return (
    <div className="brand-listt">
      <div className="header">
        <div>
          <h2>Brand List</h2>
          <p>View/Manage your brand list</p>
        </div>
        <button className="add-buttonn" onClick={() => navigate("/Brands")}>
          + Add New Brand
        </button>
      </div>

      {/* Search Bar */}
      <div className="search-barr">
        <input
          type="text"
          placeholder="🔍 Search..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th><input type="checkbox" /></th>
              <th>Image</th>
              <th onClick={() => sortData("name")}>
                Brand Name {sortConfig.key === "name" && (sortConfig.direction === "asc" ? "🔼" : "🔽")}
              </th>
              <th onClick={() => sortData("Description")}>
                Description {sortConfig.key === "Description" && (sortConfig.direction === "asc" ? "🔼" : "🔽")}
              </th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {sortedBrands.map((brand) => (
              <tr key={brand.id}>
                <td><input type="checkbox" /></td>
                <td>
                  <img
                    src={brand.image}
                    alt={brand.name}
                    style={{ width: "50px", height: "50px", objectFit: "contain" }}
                  />
                </td>
                <td>{brand.name}</td>
                <td>{brand.Description}</td>
                <td>
                  <span className="action-icon view-icon">👁️</span>
                  <span className="action-icon edit-icon">✏️</span>
                  <span className="action-icon delete-icon">🗑️</span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default BrandList;
